import React, { useState } from "react";

import classes from "./CartOrderForm.module.css";

const CartOrderForm = (props) => {
  const [isFormValid, setIsFormValid] = useState(false);

  const formValidationHandler = (event) => {
    event.preventDefault();
    const name = event.target[0].value;
    const street = event.target[1].value;
    const postal = event.target[2].value;
    const city = event.target[3].value;
    if (
      name.trim().length > 0 &&
      street.trim().length > 0 &&
      postal.trim().length > 0 &&
      city.trim().length > 0 &&
      !isNaN(+postal)
    ) {
      setIsFormValid(true);
      const formData = {
        name: name,
        street: street,
        postal: postal,
        city: city,
      };
      console.log(formData);
    }
  };

  return (
    <form className={classes.form} onSubmit={formValidationHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name :</label>
        <input id="name" type="text" />
      </div>
      {isFormValid && <p>Please enter valid name</p>}
      <div className={classes.control}>
        <label htmlFor="street">Street :</label>
        <input id="street" type="text" />
      </div>
      {isFormValid && <p>Please enter valid street</p>}
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code :</label>
        <input id="postal" type="text" />
      </div>
      {isFormValid && <p>Please enter valid postal</p>}
      <div className={classes.control}>
        <label htmlFor="city">City :</label>
        <input id="city" type="text" />
      </div>
      {isFormValid && <p>Please enter valid city</p>}
      <button className={classes.button} onClick={props.onCancel}>
        Cancel
      </button>
      <button className={classes.button}>Confirm</button>
    </form>
  );
};

export default CartOrderForm;
